package ch01;

// 연습문제
public class DataTestEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 1. 550을 name1의 변수에 담아라
		int name1 = 550;
		// 2. 22억을 name2의 변수에 담아라
		long name2 = 2200000000L;
		// 3. A를 name3의 변수에 담아라
		char name3 = 'A';
		// 4. 실수 float 타입으로 0.12345678을 name4의 변수에 담아라
		float name4 = 0.12345678F;
		// 5. 실수 double 타입으로 200.0035를 name5의 변수에 담아라
		double name5 = 200.0035;
		// 6. 논리 자료형 isOk를 선언해라
		boolean isOk;
		// 7. isOk변수의 값을 false로 초기화 해라
		isOk = false;
		// 8. isOk변수의 값을 true로 초기화 해라
		isOk = true;
		// 9. 변수란 무엇인지 서술해라
		System.out.println("변수란, 데이터를 저장하는 메모리 공간이며 변할 수 있는 데이터를 의미합니다.");
		// 10. 기본 데이터 타입의 종류 8가지를 서술해라
		System.out.println("정수형: byte, short, int, long");
		System.out.println("실수형: float, double");
		System.out.println("문자형: char");
		System.out.println("논리형: boolean");
	}

}
